import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { addBasket } from '../actions/index.js'

const totalPrice = (props) => {
  props.basket.reduce((total, book) => (total += book.price), 0)
}

const Products = (props) => {
  console.log(props.basket)
  return (
    <div>
      <h2>
        <span>Kitap Listesi</span>
        <Link to='/basket'>Sepetim</Link>
      </h2>
      <h3>Toplam Sepet Tutarı:</h3>
      {props.bookList.map((book) => (
        <div className='book'>
          <img src={book.image} alt={book.name} />
          <div>
            <h4>{book.name}</h4>
            <p>{book.author}</p>
            <p>{book.price}₺</p>
            <button onClick={() => props.addBasket(book)}>Sepete Ekle</button>
          </div>
        </div>
      ))}
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    bookList: state.bookList,
    basket: state.basket,
  }
}

export default connect(mapStateToProps, { addBasket })(Products)
