import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

const BasketCart = (props) => {
  const totalPrice = props.basket.reduce(
    (total, product) => (total += product.price),
    0
  )
  return (
    <div>
      <h2>
        <Link to='/'>Alacaklarım</Link>
        <span>Faturam</span>
      </h2>
      <h3>Toplam Sepet Tutarı: {totalPrice.toFixed(2)}₺ </h3>
      {props.basket.map((product) => (
        <div className='product'>
          <img src={product.image} alt={product.name} />
          <div>
            <h4>{product.name}</h4>
            <p>Fiyat: &#8378;{product.price}₺</p>
            <p>Toplam: &#8378;19.99₺</p>
            <p>Sepetinizde bu kitaptan toplam 1 adet var.</p>
            <button>-</button>
            <button>Sepetten Çıkar</button>
            <button>+</button>
          </div>
        </div>
      ))}
    </div>
  )
}

export const mapStateToProps = (state) => {
  return {
    basket: state.basket,
  }
}

export default connect(mapStateToProps)(BasketCart)
