import { ADD } from '../type/index.types'

const INITIAL_STATE = {
  notes: [
    { id: 1, todo: 'Redux basic proje yap.', completed: false },
    {
      id: 2,
      todo: 'E-commerce sitesine firebase authentication ekle!',
      completed: true,
    },
  ],
}

export const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ADD:
      return {
        ...state,
        notes: [
          ...state.notes,
          {
            id: state.notes.length + 1,
            todo: action.payload,
            completed: false,
          },
        ],
      }

    default:
      return state
  }
}
