import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

const BasketCart = (props) => {
  const totalPrice = (props) => {
    props.basket.reduce((total, book) => (total += book.price), 0)
  }
  return (
    <div>
      <h2>
        <Link to='/'>Kitap Listesi</Link>
        <span>Sepetim</span>
      </h2>
      <h3>Toplam Sepet Tutarı: {totalPrice} </h3>
      {props.basket.map((addBook) => (
        <div className='book'>
          <img src={addBook.image} alt={addBook.name} />
          <div>
            <h4>{addBook.name}</h4>
            <p>Yazar: {addBook.author}</p>
            <p>Fiyat: &#8378;{addBook.price}</p>
            <p>Toplam: &#8378;19.99</p>
            <p>Sepetinizde bu kitaptan toplam {addBook.length} adet var.</p>
            <button>-</button>
            <button>Sepetten Çıkar</button>
            <button>+</button>
          </div>
        </div>
      ))}
    </div>
  )
}

export const mapStateToProps = (state) => {
  return {
    basket: state.basket,
  }
}

export default connect(mapStateToProps)(BasketCart)
