import { SHOP_DATA } from '../../shop-data'

const INITIAL_STATE = {
  bookList: SHOP_DATA,
  basket: [],
}

export const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case 'ADD_BASKET':
      return { ...state, basket: [...state.basket, action.payload] }

    default:
      return state
  }
}
